// BaseNode.js - Node Abstraction Component

import { useState } from 'react';
import { Handle, Position } from 'reactflow';
import ReactTooltip from 'react-tooltip';

export const BaseNode = ({ 
  id, 
  data, 
  title,
  icon,
  description,
  inputs = [], // Array of input handles: {id, label, position, style}
  outputs = [], // Array of output handles: {id, label, position, style}
  fields = [], // Array of form fields: {type, name, label, options, defaultValue}
  content = null, // Custom content component
  style = {},
  onFieldChange
}) => {
  const [fieldValues, setFieldValues] = useState(() => {
    const initialValues = {};
    fields.forEach(field => {
      initialValues[field.name] = data?.[field.name] || field.defaultValue || '';
    });
    return initialValues;
  });

  const handleFieldChange = (fieldName, value) => {
    setFieldValues(prev => ({
      ...prev,
      [fieldName]: value
    }));

    if (onFieldChange) {
      onFieldChange(fieldName, value);
    }
  };

  const renderField = (field) => {
    const value = fieldValues[field.name];

    switch (field.type) {
      case 'text':
        return (
          <input
            type="text"
            value={value}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            placeholder={field.placeholder}
            style={{ width: '100%', marginBottom: '4px' }}
          />
        );

      case 'textarea':
        return (
          <textarea
            value={value}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            placeholder={field.placeholder}
            rows={field.rows || 3}
            style={{ width: '100%', marginBottom: '4px', resize: 'vertical' }}
          />
        );

      case 'select':
        return (
          <select
            value={value}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            style={{ width: '100%', marginBottom: '4px' }}
          >
            {field.options?.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        );

      case 'number':
        return (
          <input
            type="number"
            value={value}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            min={field.min}
            max={field.max}
            step={field.step}
            style={{ width: '100%', marginBottom: '4px' }}
          />
        );

      default:
        return null;
    }
  };

  const defaultStyle = {
    width: 200,
    minHeight: 80,
    border: '2px solid',
    borderImage: 'linear-gradient(90deg, #A000FF 0%, #FF00B8 100%) 1',
    borderRadius: '16px',
    background: 'rgba(255,255,255,0.02)',
    boxShadow: '0 0 12px 2px #A000FF44, 0 0 24px 4px #FF00B844',
    backdropFilter: 'blur(8px)',
    WebkitBackdropFilter: 'blur(8px)',
    padding: '20px',
    fontSize: '12px',
    transition: 'all 0.2s ease',
    ':hover': {
      boxShadow: '0 4px 8px rgba(0,0,0,0.15)',
      border: '1px solid #999'
    },
    ...style
  };

  // Determine if this is a special node that needs larger title
  const isSpecialNode = ['Math', 'Conditional', 'Splitter'].includes(title);
  const titleStyle = {
    fontWeight: 'bold',
    marginBottom: '12px',
    textAlign: 'center',
    fontSize: isSpecialNode ? '16px' : '14px'
  };

  const handleStyle = {
    width: '8px',
    height: '8px',
    background: '#333',
    border: '1px solid #fff',
    transition: 'all 0.2s ease',
  };

  // const handleHoverStyle = {
  //   width: '10px',
  //   height: '10px',
  //   background: '#2196f3',
  //   border: '1px solid #fff',
  // };

  // Tooltip content
  const typeDescriptions = {
    'Input': 'Input Node — Accepts user-defined values as entry points to the pipeline.',
    'Output': 'Output Node — Outputs results from the pipeline.',
    'LLM': 'LLM Node — Generates a response from a language model based on input text.',
    'Text': 'Text Node — Allows text manipulation and variable interpolation.',
    'Math': 'Math Node — Performs mathematical operations.',
    'Filter': 'Filter Node — Filters data based on conditions.',
    'Delay': 'Delay Node — Adds a delay to the pipeline.',
    'Splitter': 'Splitter Node — Splits data into multiple outputs.',
    'Conditional': 'Conditional Node — Branches logic based on conditions.'
  };
  const tooltipContent = (
    <div style={{
      background: 'rgba(26,26,26,0.95)',
      border: '1px solid rgba(255,255,255,0.10)',
      color: '#C5AFFF',
      fontSize: '0.95em',
      padding: '10px 14px',
      borderRadius: '10px',
      boxShadow: '0 0 12px #A000FF44',
      maxWidth: 260,
      lineHeight: 1.5
    }}>
      <div style={{ fontWeight: 700, marginBottom: 4, color: '#E1D9FF', textShadow: '0 0 4px #A000FF' }}>{title} Node</div>
      <div style={{ marginBottom: 4 }}>{description || typeDescriptions[title] || ''}</div>
      {(inputs.length > 0 || outputs.length > 0) && (
        <div style={{ fontSize: '0.92em', marginTop: 6 }}>
          {inputs.length > 0 && <div><strong>Inputs:</strong> {inputs.map(i => i.label).join(', ')}</div>}
          {outputs.length > 0 && <div><strong>Outputs:</strong> {outputs.map(o => o.label).join(', ')}</div>}
        </div>
      )}
    </div>
  );

  return (
    <>
      <div
        data-tip
        data-for={`node-tooltip-${id}`}
        style={defaultStyle}
        onMouseEnter={(e) => {
          e.currentTarget.style.boxShadow = '0 0 24px 4px #A000FF88, 0 0 48px 8px #FF00B888';
          e.currentTarget.style.border = '2px solid #FF00B8';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.boxShadow = '0 0 12px 2px #A000FF44, 0 0 24px 4px #FF00B844';
          e.currentTarget.style.border = '2px solid';
          e.currentTarget.style.borderImage = 'linear-gradient(90deg, #A000FF 0%, #FF00B8 100%) 1';
        }}
      >
        {/* Input Handles */}
        {inputs.map((input, index) => (
          <Handle
            key={input.id}
            type="target"
            position={input.position || Position.Left}
            id={`${id}-${input.id}`}
            style={{
              ...handleStyle,
              top: inputs.length > 1 ? `${((index + 1) * 100) / (inputs.length + 1)}%` : '50%',
              ...input.style
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.width = '10px';
              e.currentTarget.style.height = '10px';
              e.currentTarget.style.background = '#2196f3';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.width = '8px';
              e.currentTarget.style.height = '8px';
              e.currentTarget.style.background = '#333';
            }}
          />
        ))}

        {/* Node Header */}
        <div style={titleStyle}>
          {title}
        </div>

        {/* Custom Content */}
        {content && <div style={{ marginBottom: '12px' }}>{content}</div>}

        {/* Form Fields */}
        {fields.map(field => (
          <div key={field.name} style={{ marginBottom: '10px' }}>
            {field.label && (
              <label style={{ 
                display: 'block', 
                fontSize: '11px', 
                marginBottom: '4px',
                color: '#666'
              }}>
                {field.label}:
              </label>
            )}
            {renderField(field)}
            {field.name === 'delimiter' && (
              <div style={{ 
                fontSize: '10px', 
                color: '#888', 
                marginTop: '4px',
                fontStyle: 'italic'
              }}>
                Enter a character to split text (e.g., comma, space, newline)
              </div>
            )}
          </div>
        ))}

        {/* Output Handles */}
        {outputs.map((output, index) => (
          <Handle
            key={output.id}
            type="source"
            position={output.position || Position.Right}
            id={`${id}-${output.id}`}
            style={{
              ...handleStyle,
              top: outputs.length > 1 ? `${((index + 1) * 100) / (outputs.length + 1)}%` : '50%',
              ...output.style
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.width = '10px';
              e.currentTarget.style.height = '10px';
              e.currentTarget.style.background = '#2196f3';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.width = '8px';
              e.currentTarget.style.height = '8px';
              e.currentTarget.style.background = '#333';
            }}
          />
        ))}
      </div>
      <ReactTooltip
        id={`node-tooltip-${id}`}
        effect="solid"
        delayShow={300}
        place="top"
        backgroundColor="rgba(26,26,26,0.95)"
        border={true}
        borderColor="rgba(255,255,255,0.10)"
        arrowColor="rgba(26,26,26,0.95)"
        clickable={false}
        className="vectorshift-tooltip"
      >
        {tooltipContent}
      </ReactTooltip>
    </>
  );
};
