// submit.js
import { toast } from 'react-toastify';

export const SubmitButton = ({ nodes, edges }) => {
    const handleSubmit = async (e) => {
        // Handle button press animation
        e.currentTarget.style.transform = 'translateY(0)';
        e.currentTarget.style.boxShadow = '0 2px 4px rgba(33, 150, 243, 0.3)';

        try {
            const response = await fetch('http://localhost:8000/pipelines/parse', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    nodes: nodes,
                    edges: edges
                })
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            
            // Create toast message
            toast.success(
                <div>
                    <div style={{ fontWeight: 'bold', marginBottom: '8px' }}>üîî Pipeline submitted successfully!</div>
                    <div>Nodes: {data.num_nodes}</div>
                    <div>Edges: {data.num_edges}</div>
                    <div>Is DAG: {data.is_dag ? 'Yes' : 'No'}</div>
                </div>,
                {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                }
            );
            
        } catch (error) {
            console.error('Error submitting pipeline:', error);
            toast.error(
                <div>
                    <div style={{ fontWeight: 'bold', marginBottom: '8px' }}>‚ùå Error submitting pipeline</div>
                    <div>{error.message}</div>
                </div>,
                {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                }
            );
        }
    };

    return (
        <div style={{
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center',
            padding: '20px',
            marginTop: '20px'
        }}>
            <button 
                type="button"
                onClick={handleSubmit}
                style={{
                    padding: '12px 24px',
                    fontSize: '16px',
                    fontWeight: '600',
                    color: '#ffffff',
                    backgroundColor: '#2196F3',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 2px 4px rgba(33, 150, 243, 0.3)',
                    ':hover': {
                        backgroundColor: '#1976D2',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 4px 8px rgba(33, 150, 243, 0.4)'
                    },
                    ':active': {
                        transform: 'translateY(0)',
                        boxShadow: '0 2px 4px rgba(33, 150, 243, 0.3)'
                    }
                }}
                onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = '#1976D2';
                    e.currentTarget.style.transform = 'translateY(-2px)';
                    e.currentTarget.style.boxShadow = '0 4px 8px rgba(33, 150, 243, 0.4)';
                }}
                onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = '#2196F3';
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.boxShadow = '0 2px 4px rgba(33, 150, 243, 0.3)';
                }}
            >
                Submit Pipeline
            </button>
        </div>
    );
}
