// submit.js
import { toast } from 'react-toastify';
import { motion, AnimatePresence } from 'framer-motion';

export const SubmitButton = ({ nodes, edges }) => {
    const handleSubmit = async (e) => {
        // Handle button press animation
        e.currentTarget.style.transform = 'translateY(0)';
        e.currentTarget.style.boxShadow = '0 2px 4px rgba(33, 150, 243, 0.3)';

        // Show loading toast
        const loadingToast = toast.info('Submitting pipeline...', {
            position: "top-right",
            autoClose: false,
            hideProgressBar: false,
            closeOnClick: false,
            pauseOnHover: false,
            draggable: false,
        });

        try {
            console.log('Submitting pipeline with:', { nodes: nodes.length, edges: edges.length });
            
            const response = await fetch('http://localhost:8000/pipelines/parse', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    nodes: nodes,
                    edges: edges
                })
            });

            // Dismiss loading toast
            toast.dismiss(loadingToast);

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            console.log('Backend response:', data);
            
            // Create success toast message
            toast.success(
                <AnimatePresence>
                  <motion.div
                    initial={{ opacity: 0, y: 30, scale: 0.95 }}
                    animate={{ opacity: 1, y: 0, scale: 1 }}
                    exit={{ opacity: 0, y: 30, scale: 0.95 }}
                    transition={{ duration: 0.4, type: 'spring' }}
                    style={{
                      background: '#18122B',
                      boxShadow: '0 0 24px 4px #A000FF88',
                      borderRadius: '18px',
                      padding: '28px 32px',
                      color: '#F5F5F5',
                      fontFamily: 'Inter, Sora, Poppins, Space Grotesk, Segoe UI, Arial, sans-serif',
                      minWidth: 260,
                      maxWidth: 400,
                      margin: '0 auto',
                      textAlign: 'center',
                      letterSpacing: '0.01em',
                    }}
                  >
                    <div style={{
                      fontWeight: 800,
                      fontSize: '1.5rem',
                      marginBottom: 12,
                      color: '#C5AFFF',
                      textShadow: '0 0 8px #A000FF, 0 0 16px #FF00B8',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      gap: 8
                    }}>
                      <span role="img" aria-label="celebrate">🎉</span> Pipeline Analysis Complete!
                    </div>
                    <div style={{
                      marginBottom: 8,
                      fontSize: '1.1rem',
                      display: 'flex',
                      alignItems: 'center',
                      gap: 8,
                      justifyContent: 'center',
                    }}>
                      <span role="img" aria-label="nodes">🟣</span>
                      <span><strong>Nodes:</strong> {data.num_nodes}</span>
                    </div>
                    <div style={{
                      marginBottom: 8,
                      fontSize: '1.1rem',
                      display: 'flex',
                      alignItems: 'center',
                      gap: 8,
                      justifyContent: 'center',
                    }}>
                      <span role="img" aria-label="edges">🔗</span>
                      <span><strong>Edges:</strong> {data.num_edges}</span>
                    </div>
                    <div style={{
                      marginBottom: 8,
                      fontSize: '1.1rem',
                      display: 'flex',
                      alignItems: 'center',
                      gap: 8,
                      justifyContent: 'center',
                    }}>
                      <span role="img" aria-label="dag">{data.is_dag ? '✅' : '❌'}</span>
                      <span><strong>Is DAG:</strong> {data.is_dag ? 'Yes' : 'No (Contains cycles)'}</span>
                    </div>
                    {!data.is_dag && (
                      <div style={{
                        fontSize: '1rem',
                        color: '#FF6B6B',
                        marginTop: 10,
                        fontStyle: 'italic',
                        textShadow: '0 0 4px #FF00B8',
                      }}>
                        Warning: Your pipeline contains cycles which may cause infinite loops.
                      </div>
                    )}
                  </motion.div>
                </AnimatePresence>,
                {
                    position: "top-right",
                    autoClose: 7000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                }
            );
            
        } catch (error) {
            console.error('Error submitting pipeline:', error);
            
            // Dismiss loading toast
            toast.dismiss(loadingToast);
            
            toast.error(
                <div>
                    <div style={{ fontWeight: 'bold', marginBottom: '8px' }}>
                        ❌ Error submitting pipeline
                    </div>
                    <div style={{ marginBottom: '4px' }}>
                        {error.message}
                    </div>
                    <div style={{ 
                        fontSize: '12px', 
                        color: '#666', 
                        marginTop: '8px',
                        fontStyle: 'italic'
                    }}>
                        Make sure the backend server is running on http://localhost:8000
                    </div>
                </div>,
                {
                    position: "top-right",
                    autoClose: 7000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                }
            );
        }
    };

    return (
        <div style={{
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center',
            padding: '20px',
            marginTop: '20px'
        }}>
            <button 
                type="button"
                onClick={handleSubmit}
                disabled={nodes.length === 0}
                style={{
                    padding: '12px 24px',
                    fontSize: '16px',
                    fontWeight: '600',
                    color: '#ffffff',
                    backgroundColor: nodes.length === 0 ? '#cccccc' : '#2196F3',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: nodes.length === 0 ? 'not-allowed' : 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 2px 4px rgba(33, 150, 243, 0.3)',
                    opacity: nodes.length === 0 ? 0.6 : 1,
                }}
                onMouseEnter={(e) => {
                    if (nodes.length > 0) {
                        e.currentTarget.style.backgroundColor = '#1976D2';
                        e.currentTarget.style.transform = 'translateY(-2px)';
                        e.currentTarget.style.boxShadow = '0 4px 8px rgba(33, 150, 243, 0.4)';
                    }
                }}
                onMouseLeave={(e) => {
                    if (nodes.length > 0) {
                        e.currentTarget.style.backgroundColor = '#2196F3';
                        e.currentTarget.style.transform = 'translateY(0)';
                        e.currentTarget.style.boxShadow = '0 2px 4px rgba(33, 150, 243, 0.3)';
                    }
                }}
            >
                {nodes.length === 0 ? 'Add nodes to submit' : 'Submit Pipeline'}
            </button>
        </div>
    );
}
