// submit.js
import { toast } from 'react-toastify';
import { motion, AnimatePresence } from 'framer-motion';

export const SubmitButton = ({ nodes, edges }) => {
    const handleSubmit = async (e) => {
        // Handle button press animation
        e.currentTarget.style.transform = 'translateY(0)';
        e.currentTarget.style.boxShadow = '0 2px 4px rgba(33, 150, 243, 0.3)';

        // Show loading toast
        const loadingToast = toast.info('Submitting pipeline...', {
            position: "top-right",
            autoClose: false,
            hideProgressBar: false,
            closeOnClick: false,
            pauseOnHover: false,
            draggable: false,
        });

        try {
            console.log('Submitting pipeline with:', { nodes: nodes.length, edges: edges.length });

            const response = await fetch('http://localhost:8000/pipelines/parse', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    nodes: nodes,
                    edges: edges
                })
            });

            // Dismiss loading toast
            toast.dismiss(loadingToast);

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            console.log('Backend response:', data);

            // Create success toast message
            toast.success(
                <div
                  className="alert-box"
                  style={{
                    background: 'rgba(255,255,255,0.13)',
                    boxShadow: '0 8px 32px 0 #A000FF22, 0 2px 8px #0002',
                    borderRadius: '18px',
                    padding: '32px 0 28px 0',
                    color: '#232042',
                    fontFamily: 'Inter, Sora, Poppins, Space Grotesk, Segoe UI, Arial, sans-serif',
                    minWidth: 340,
                    maxWidth: 420,
                    margin: '0 auto',
                    textAlign: 'center',
                    letterSpacing: '0.01em',
                    border: 'none',
                    position: 'relative',
                    overflow: 'hidden',
                  }}
                >
                  {/* Left accent bar */}
                  <div style={{
                    position: 'absolute',
                    left: 0,
                    top: 0,
                    bottom: 0,
                    width: 8,
                    borderTopLeftRadius: 18,
                    borderBottomLeftRadius: 18,
                    background: data.is_dag
                      ? 'linear-gradient(180deg, #00FFB8 0%, #A000FF 100%)'
                      : 'linear-gradient(180deg, #FF6B6B 0%, #FF00B8 100%)',
                  }} />
                  {/* Icon and Title */}
                  <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    marginBottom: 10,
                    gap: 18,
                  }}>
                    <span style={{
                      width: 48,
                      height: 48,
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      borderRadius: '50%',
                      background: data.is_dag
                        ? 'linear-gradient(135deg, #00FFB8 60%, #A000FF 100%)'
                        : 'linear-gradient(135deg, #FF6B6B 60%, #FF00B8 100%)',
                      boxShadow: data.is_dag
                        ? '0 0 12px #00FFB844'
                        : '0 0 12px #FF6B6B44',
                    }}>
                      {data.is_dag ? (
                        <svg width="28" height="28" viewBox="0 0 28 28" fill="none" xmlns="http://www.w3.org/2000/svg">
                          <circle cx="14" cy="14" r="14" fill="#fff" fillOpacity="0.18"/>
                          <path d="M8.5 15.5L13 20L20 11" stroke="#00FFB8" strokeWidth="2.2" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                      ) : (
                        <svg width="28" height="28" viewBox="0 0 28 28" fill="none" xmlns="http://www.w3.org/2000/svg">
                          <circle cx="14" cy="14" r="14" fill="#fff" fillOpacity="0.18"/>
                          <path d="M9 19L19 9M9 9L19 19" stroke="#FF6B6B" strokeWidth="2.2" strokeLinecap="round"/>
                        </svg>
                      )}
                    </span>
                    <div style={{ textAlign: 'left' }}>
                      <div style={{
                        fontWeight: 900,
                        fontSize: '1.35rem',
                        color: '#232042',
                        letterSpacing: '0.01em',
                        marginBottom: 2,
                      }}>
                        Pipeline Analysis
                      </div>
                      <div style={{
                        fontWeight: 500,
                        fontSize: '1.01rem',
                        color: '#7B7B8B',
                        letterSpacing: '0.01em',
                        marginTop: 0,
                      }}>
                        {data.is_dag ? 'Your pipeline is a valid Directed Acyclic Graph.' : 'Cycles detected in your pipeline.'}
                      </div>
                    </div>
                  </div>
                  {/* Divider */}
                  <div style={{
                    height: 1,
                    width: '90%',
                    background: 'linear-gradient(90deg, #A000FF22 0%, #FF00B822 100%)',
                    margin: '18px auto 18px auto',
                    border: 'none',
                  }} />
                  {/* Stats grid */}
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: '1fr 1fr 1fr',
                    gap: 12,
                    margin: '0 auto 10px auto',
                    justifyItems: 'center',
                  }}>
                    <div style={{
                      background: 'rgba(160,0,255,0.10)',
                      color: '#A000FF',
                      borderRadius: 8,
                      padding: '8px 0',
                      width: 90,
                      fontWeight: 700,
                      fontSize: '1.01rem',
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      boxShadow: '0 0 4px #A000FF22',
                    }}>
                      <span style={{fontSize: 18, marginBottom: 2}}>üü£</span>
                      <span style={{fontSize: 13, color: '#7B7B8B', fontWeight: 500}}>Nodes</span>
                      <span style={{fontSize: 16, fontWeight: 700}}>{data.num_nodes}</span>
                    </div>
                    <div style={{
                      background: 'rgba(0,255,184,0.10)',
                      color: '#00FFB8',
                      borderRadius: 8,
                      padding: '8px 0',
                      width: 90,
                      fontWeight: 700,
                      fontSize: '1.01rem',
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      boxShadow: '0 0 4px #00FFB822',
                    }}>
                      <span style={{fontSize: 18, marginBottom: 2}}>üîó</span>
                      <span style={{fontSize: 13, color: '#7B7B8B', fontWeight: 500}}>Edges</span>
                      <span style={{fontSize: 16, fontWeight: 700}}>{data.num_edges}</span>
                    </div>
                    <div style={{
                      background: data.is_dag ? 'rgba(0,255,184,0.13)' : 'rgba(255,107,107,0.13)',
                      color: data.is_dag ? '#00FFB8' : '#FF6B6B',
                      borderRadius: 8,
                      padding: '8px 0',
                      width: 90,
                      fontWeight: 700,
                      fontSize: '1.01rem',
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      boxShadow: data.is_dag ? '0 0 4px #00FFB822' : '0 0 4px #FF6B6B22',
                    }}>
                      <span style={{fontSize: 18, marginBottom: 2}}>{data.is_dag ? '‚úÖ' : '‚ùå'}</span>
                      <span style={{fontSize: 13, color: '#7B7B8B', fontWeight: 500}}>DAG</span>
                      <span style={{fontSize: 16, fontWeight: 700}}>{data.is_dag ? 'Yes' : 'No'}</span>
                    </div>
                  </div>
                  {/* Subtle footer message for cycles */}
                  {!data.is_dag && (
                    <div style={{
                      fontSize: '1.01rem',
                      color: '#FF6B6B',
                      fontWeight: 500,
                      marginTop: 10,
                      textShadow: '0 0 4px #FF00B8',
                      letterSpacing: '0.01em',
                    }}>
                      Warning: Your pipeline contains cycles which may cause infinite loops.
                    </div>
                  )}
                </div>,
                {
                    position: "top-right",
                    autoClose: 7000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                }
            );

        } catch (error) {
            console.error('Error submitting pipeline:', error);

            // Dismiss loading toast
            toast.dismiss(loadingToast);

            toast.error(
                <div>
                    <div style={{ fontWeight: 'bold', marginBottom: '8px' }}>
                        ‚ùå Error submitting pipeline
                    </div>
                    <div style={{ marginBottom: '4px' }}>
                        {error.message}
                    </div>
                    <div style={{ 
                        fontSize: '12px', 
                        color: '#666', 
                        marginTop: '8px',
                        fontStyle: 'italic'
                    }}>
                        Make sure the backend server is running on http://localhost:8000
                    </div>
                </div>,
                {
                    position: "top-right",
                    autoClose: 7000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                }
            );
        }
    };

    return (
        <div style={{
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center',
            padding: '20px',
            marginTop: '20px'
        }}>
            <button 
                type="button"
                onClick={handleSubmit}
                disabled={nodes.length === 0}
                style={{
                    padding: '12px 24px',
                    fontSize: '16px',
                    fontWeight: '600',
                    color: '#ffffff',
                    backgroundColor: nodes.length === 0 ? '#cccccc' : '#2196F3',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: nodes.length === 0 ? 'not-allowed' : 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 2px 4px rgba(33, 150, 243, 0.3)',
                    opacity: nodes.length === 0 ? 0.6 : 1,
                }}
                onMouseEnter={(e) => {
                    if (nodes.length > 0) {
                        e.currentTarget.style.backgroundColor = '#1976D2';
                        e.currentTarget.style.transform = 'translateY(-2px)';
                        e.currentTarget.style.boxShadow = '0 4px 8px rgba(33, 150, 243, 0.4)';
                    }
                }}
                onMouseLeave={(e) => {
                    if (nodes.length > 0) {
                        e.currentTarget.style.backgroundColor = '#2196F3';
                        e.currentTarget.style.transform = 'translateY(0)';
                        e.currentTarget.style.boxShadow = '0 2px 4px rgba(33, 150, 243, 0.3)';
                    }
                }}
            >
                {nodes.length === 0 ? 'Add nodes to submit' : 'Submit Pipeline'}
            </button>
        </div>
    );
}
