import React from 'react';
import { getBezierPath } from 'reactflow';

export default function CustomRemovableEdge({ id, sourceX, sourceY, targetX, targetY, style, markerEnd, data }) {
  const [edgePath, labelX, labelY] = getBezierPath({ sourceX, sourceY, targetX, targetY });
  const [hovered, setHovered] = React.useState(false);

  // Remove edge handler
  const handleRemove = (e) => {
    e.stopPropagation();
    if (data && typeof data.onRemove === 'function') {
      data.onRemove(id);
    }
  };

  return (
    <g
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      style={{ cursor: 'pointer' }}
    >
      <path
        id={id}
        style={{ ...style, stroke: '#A000FF', strokeWidth: 2, filter: 'drop-shadow(0 0 6px #A000FF)' }}
        className="react-flow__edge-path"
        d={edgePath}
        markerEnd={markerEnd}
      />
      {hovered && (
        <foreignObject
          x={labelX - 12}
          y={labelY - 12}
          width={24}
          height={24}
          style={{ pointerEvents: 'all' }}
        >
          <div
            onClick={handleRemove}
            style={{
              width: 24,
              height: 24,
              background: 'rgba(24,18,43,0.95)',
              border: '4px solid #A000FF',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: '#FF00B8',
              fontWeight: 900,
              fontSize: 16,
              boxShadow: '0 0 8px #A000FF',
              transition: 'box-shadow 0.2s',
              cursor: 'pointer',
              userSelect: 'none',
            }}
            title="Remove connection"
          >
            âœ•
          </div>
        </foreignObject>
      )}
    </g>
  );
}
