// BaseNode.js - Node Abstraction Component

import { useState } from 'react';
import { Handle, Position } from 'reactflow';
import { motion } from 'framer-motion';

export const BaseNode = ({ 
  id, 
  data, 
  title,
  icon,
  inputs = [], // Array of input handles: {id, label, position, style}
  outputs = [], // Array of output handles: {id, label, position, style}
  fields = [], // Array of form fields: {type, name, label, options, defaultValue}
  content = null, // Custom content component
  style = {},
  onFieldChange
}) => {
  const [fieldValues, setFieldValues] = useState(() => {
    const initialValues = {};
    fields.forEach(field => {
      initialValues[field.name] = data?.[field.name] || field.defaultValue || '';
    });
    return initialValues;
  });

  const handleFieldChange = (fieldName, value) => {
    setFieldValues(prev => ({
      ...prev,
      [fieldName]: value
    }));

    if (onFieldChange) {
      onFieldChange(fieldName, value);
    }
  };

  const renderField = (field) => {
    const value = fieldValues[field.name];

    switch (field.type) {
      case 'text':
        return (
          <input
            type="text"
            value={value}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            placeholder={field.placeholder}
            style={{ width: '100%', marginBottom: '4px' }}
          />
        );

      case 'textarea':
        return (
          <textarea
            value={value}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            placeholder={field.placeholder}
            rows={field.rows || 3}
            style={{ width: '100%', marginBottom: '4px', resize: 'vertical' }}
          />
        );

      case 'select':
        return (
          <select
            value={value}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            style={{ width: '100%', marginBottom: '4px' }}
          >
            {field.options?.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        );

      case 'number':
        return (
          <input
            type="number"
            value={value}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            min={field.min}
            max={field.max}
            step={field.step}
            style={{ width: '100%', marginBottom: '4px' }}
          />
        );

      default:
        return null;
    }
  };

  const defaultStyle = {
    width: 'min(96vw, 340px)',
    minHeight: 80,
    border: '2px solid',
    borderImage: 'linear-gradient(90deg, #A000FF 0%, #FF00B8 100%) 1',
    borderRadius: '16px',
    background: '#18122B',
    boxShadow: '0 0 24px 4px #A000FF88',
    color: '#C5AFFF',
    fontFamily: 'Inter, Sora, Poppins, Space Grotesk, Segoe UI, Arial, sans-serif',
    padding: '24px 20px 20px 20px',
    fontSize: '13px',
    transition: 'all 0.2s ease',
    margin: '0 auto',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'stretch',
    ...style
  };

  const titleStyle = {
    fontWeight: 800,
    marginBottom: '16px',
    textAlign: 'center',
    fontSize: '1.3rem',
    color: '#E1D9FF',
    letterSpacing: '0.01em',
    textShadow: '0 0 8px #A000FF, 0 0 16px #FF00B8',
    fontFamily: 'Inter, Sora, Poppins, Space Grotesk, Segoe UI, Arial, sans-serif',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: 8
  };

  const handleStyle = {
    width: '8px',
    height: '8px',
    background: '#333',
    border: '1px solid #fff',
    transition: 'all 0.2s ease',
  };

  // const handleHoverStyle = {
  //   width: '10px',
  //   height: '10px',
  //   background: '#2196f3',
  //   border: '1px solid #fff',
  // };

  return (
    <motion.div
      initial={{ opacity: 0, y: 30, scale: 0.97 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      exit={{ opacity: 0, y: 30, scale: 0.97 }}
      transition={{ duration: 0.4, type: 'spring' }}
      style={defaultStyle}
      onMouseEnter={(e) => {
        e.currentTarget.style.boxShadow = '0 0 32px 8px #A000FFcc, 0 0 48px 12px #FF00B8cc';
        e.currentTarget.style.border = '2px solid #FF00B8';
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.boxShadow = '0 0 24px 4px #A000FF88';
        e.currentTarget.style.border = '2px solid';
        e.currentTarget.style.borderImage = 'linear-gradient(90deg, #A000FF 0%, #FF00B8 100%) 1';
      }}
    >
      {/* Input Handles */}
      {inputs.map((input, index) => (
        <Handle
          key={input.id}
          type="target"
          position={input.position || Position.Left}
          id={`${id}-${input.id}`}
          style={{
            ...handleStyle,
            top: inputs.length > 1 ? `${((index + 1) * 100) / (inputs.length + 1)}%` : '50%',
            ...input.style
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.width = '10px';
            e.currentTarget.style.height = '10px';
            e.currentTarget.style.background = '#A000FF';
            e.currentTarget.style.boxShadow = '0 0 8px #A000FF, 0 0 16px #FF00B8';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.width = '8px';
            e.currentTarget.style.height = '8px';
            e.currentTarget.style.background = '#333';
            e.currentTarget.style.boxShadow = '0 0 8px #A000FF44, 0 0 16px #FF00B844';
          }}
        />
      ))}

      {/* Node Header */}
      <div style={titleStyle}>
        {icon && <span>{icon}</span>}
        {title}
      </div>

      {/* Custom Content */}
      {content && <div style={{ marginBottom: '12px' }}>{content}</div>}

      {/* Form Fields */}
      {fields.map(field => (
        <div key={field.name} style={{ marginBottom: '10px' }}>
          {field.label && (
            <label style={{ 
              display: 'block', 
              fontSize: '11px', 
              marginBottom: '4px',
              color: '#C5AFFF',
              fontWeight: 500
            }}>
              {field.label}:
            </label>
          )}
          {renderField(field)}
          {field.name === 'delimiter' && (
            <div style={{ 
              fontSize: '10px', 
              color: '#888', 
              marginTop: '4px',
              fontStyle: 'italic'
            }}>
              Enter a character to split text (e.g., comma, space, newline)
            </div>
          )}
        </div>
      ))}

      {/* Output Handles */}
      {outputs.map((output, index) => (
        <Handle
          key={output.id}
          type="source"
          position={output.position || Position.Right}
          id={`${id}-${output.id}`}
          style={{
            ...handleStyle,
            top: outputs.length > 1 ? `${((index + 1) * 100) / (outputs.length + 1)}%` : '50%',
            ...output.style
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.width = '10px';
            e.currentTarget.style.height = '10px';
            e.currentTarget.style.background = '#A000FF';
            e.currentTarget.style.boxShadow = '0 0 8px #A000FF, 0 0 16px #FF00B8';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.width = '8px';
            e.currentTarget.style.height = '8px';
            e.currentTarget.style.background = '#333';
            e.currentTarget.style.boxShadow = '0 0 8px #A000FF44, 0 0 16px #FF00B844';
          }}
        />
      ))}
    </motion.div>
  );
};
