// submit.js
import { toast } from 'react-toastify';
import { motion, AnimatePresence } from 'framer-motion';

export const SubmitButton = ({ nodes, edges }) => {
    const handleSubmit = async (e) => {
        // Handle button press animation
        e.currentTarget.style.transform = 'translateY(0)';
        e.currentTarget.style.boxShadow = '0 2px 4px rgba(33, 150, 243, 0.3)';

        // Show loading toast
        const loadingToast = toast.info('Submitting pipeline...', {
            position: "top-right",
            autoClose: false,
            hideProgressBar: false,
            closeOnClick: false,
            pauseOnHover: false,
            draggable: false,
        });

        try {
            console.log('Submitting pipeline with:', { nodes: nodes.length, edges: edges.length });

            const response = await fetch('http://localhost:8000/pipelines/parse', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    nodes: nodes,
                    edges: edges
                })
            });

            // Dismiss loading toast
            toast.dismiss(loadingToast);

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            console.log('Backend response:', data);

            // Create success toast message
            toast.success(
                <div
                  className="alert-box"
                  style={{
                    background: 'linear-gradient(135deg, rgba(24,18,43,0.92) 60%, rgba(33,22,60,0.98) 100%)',
                    boxShadow: '0 8px 32px 0 #A000FF33, 0 0 0 2px #A000FF22',
                    borderRadius: '20px',
                    padding: '36px 40px 28px 40px',
                    color: '#F5F5F5',
                    fontFamily: 'Inter, Sora, Poppins, Space Grotesk, Segoe UI, Arial, sans-serif',
                    minWidth: 320,
                    maxWidth: 440,
                    margin: '0 auto',
                    textAlign: 'center',
                    letterSpacing: '0.01em',
                    border: '1.5px solid #2D1A4D',
                    backdropFilter: 'blur(10px)',
                    position: 'relative',
                  }}
                >
                  <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    marginBottom: 18,
                  }}>
                    <span style={{
                      fontSize: 44,
                      marginRight: 18,
                      color: data.is_dag ? '#00FFB8' : '#FF6B6B',
                      filter: data.is_dag
                        ? 'drop-shadow(0 0 8px #00FFB8)'
                        : 'drop-shadow(0 0 8px #FF6B6B)',
                      display: 'flex',
                      alignItems: 'center',
                    }}>
                      {data.is_dag ? (
                        <svg width="40" height="40" viewBox="0 0 40 40" fill="none" xmlns="http://www.w3.org/2000/svg">
                          <circle cx="20" cy="20" r="20" fill="#00FFB8" fillOpacity="0.18"/>
                          <path d="M13 21.5L18 26.5L27 15.5" stroke="#00FFB8" strokeWidth="3.2" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                      ) : (
                        <svg width="40" height="40" viewBox="0 0 40 40" fill="none" xmlns="http://www.w3.org/2000/svg">
                          <circle cx="20" cy="20" r="20" fill="#FF6B6B" fillOpacity="0.18"/>
                          <path d="M14 26L26 14M14 14L26 26" stroke="#FF6B6B" strokeWidth="3.2" strokeLinecap="round"/>
                        </svg>
                      )}
                    </span>
                    <span style={{
                      fontWeight: 900,
                      fontSize: '2rem',
                      background: 'linear-gradient(90deg, #A000FF 0%, #FF00B8 100%)',
                      WebkitBackgroundClip: 'text',
                      WebkitTextFillColor: 'transparent',
                      letterSpacing: '0.01em',
                      textShadow: '0 0 12px #A000FF44, 0 0 24px #FF00B844',
                      display: 'block',
                    }}>
                      Pipeline Analysis
                    </span>
                  </div>
                  <div style={{
                    height: 1,
                    width: '100%',
                    background: 'linear-gradient(90deg, #A000FF33 0%, #FF00B833 100%)',
                    margin: '0 0 18px 0',
                    border: 'none',
                  }} />
                  <div style={{
                    display: 'flex',
                    justifyContent: 'center',
                    gap: 18,
                    marginBottom: 18,
                  }}>
                    <span style={{
                      background: 'rgba(160,0,255,0.12)',
                      color: '#C5AFFF',
                      borderRadius: 10,
                      padding: '8px 18px',
                      fontWeight: 700,
                      fontSize: '1.08rem',
                      boxShadow: '0 0 8px #A000FF22',
                      display: 'flex',
                      alignItems: 'center',
                      gap: 8,
                    }}>
                      <span style={{fontSize: 18}}>üü£</span> <span>Nodes: {data.num_nodes}</span>
                    </span>
                    <span style={{
                      background: 'rgba(0,255,184,0.10)',
                      color: '#00FFB8',
                      borderRadius: 10,
                      padding: '8px 18px',
                      fontWeight: 700,
                      fontSize: '1.08rem',
                      boxShadow: '0 0 8px #00FFB822',
                      display: 'flex',
                      alignItems: 'center',
                      gap: 8,
                    }}>
                      <span style={{fontSize: 18}}>üîó</span> <span>Edges: {data.num_edges}</span>
                    </span>
                    <span style={{
                      background: data.is_dag
                        ? 'rgba(0,255,184,0.13)'
                        : 'rgba(255,107,107,0.13)',
                      color: data.is_dag ? '#00FFB8' : '#FF6B6B',
                      borderRadius: 10,
                      padding: '8px 18px',
                      fontWeight: 700,
                      fontSize: '1.08rem',
                      boxShadow: data.is_dag
                        ? '0 0 8px #00FFB822'
                        : '0 0 8px #FF6B6B22',
                      display: 'flex',
                      alignItems: 'center',
                      gap: 8,
                    }}>
                      <span style={{fontSize: 18}}>{data.is_dag ? '‚úÖ' : '‚ùå'}</span> <span>DAG: {data.is_dag ? 'Yes' : 'No'}</span>
                    </span>
                  </div>
                  {data.is_dag ? (
                    <div style={{
                      fontSize: '1.13rem',
                      color: '#00FFB8',
                      fontWeight: 600,
                      marginTop: 8,
                      textShadow: '0 0 8px #00FFB8',
                      letterSpacing: '0.01em',
                    }}>
                      Your pipeline is a valid Directed Acyclic Graph!
                    </div>
                  ) : (
                    <div style={{
                      fontSize: '1.13rem',
                      color: '#FF6B6B',
                      fontWeight: 600,
                      marginTop: 8,
                      textShadow: '0 0 8px #FF00B8',
                      letterSpacing: '0.01em',
                    }}>
                      Warning: Your pipeline contains cycles which may cause infinite loops.
                    </div>
                  )}
                </div>,
                {
                    position: "top-right",
                    autoClose: 7000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                }
            );

        } catch (error) {
            console.error('Error submitting pipeline:', error);

            // Dismiss loading toast
            toast.dismiss(loadingToast);

            toast.error(
                <div>
                    <div style={{ fontWeight: 'bold', marginBottom: '8px' }}>
                        ‚ùå Error submitting pipeline
                    </div>
                    <div style={{ marginBottom: '4px' }}>
                        {error.message}
                    </div>
                    <div style={{ 
                        fontSize: '12px', 
                        color: '#666', 
                        marginTop: '8px',
                        fontStyle: 'italic'
                    }}>
                        Make sure the backend server is running on http://localhost:8000
                    </div>
                </div>,
                {
                    position: "top-right",
                    autoClose: 7000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                }
            );
        }
    };

    return (
        <div style={{
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center',
            padding: '20px',
            marginTop: '20px'
        }}>
            <button 
                type="button"
                onClick={handleSubmit}
                disabled={nodes.length === 0}
                style={{
                    padding: '12px 24px',
                    fontSize: '16px',
                    fontWeight: '600',
                    color: '#ffffff',
                    backgroundColor: nodes.length === 0 ? '#cccccc' : '#2196F3',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: nodes.length === 0 ? 'not-allowed' : 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 2px 4px rgba(33, 150, 243, 0.3)',
                    opacity: nodes.length === 0 ? 0.6 : 1,
                }}
                onMouseEnter={(e) => {
                    if (nodes.length > 0) {
                        e.currentTarget.style.backgroundColor = '#1976D2';
                        e.currentTarget.style.transform = 'translateY(-2px)';
                        e.currentTarget.style.boxShadow = '0 4px 8px rgba(33, 150, 243, 0.4)';
                    }
                }}
                onMouseLeave={(e) => {
                    if (nodes.length > 0) {
                        e.currentTarget.style.backgroundColor = '#2196F3';
                        e.currentTarget.style.transform = 'translateY(0)';
                        e.currentTarget.style.boxShadow = '0 2px 4px rgba(33, 150, 243, 0.3)';
                    }
                }}
            >
                {nodes.length === 0 ? 'Add nodes to submit' : 'Submit Pipeline'}
            </button>
        </div>
    );
}
